import React, { Component } from 'react';
import { FormField, ValidationRule } from '../types';
import DragDropUI from './DragDropUI';
import LivePreview from './LivePreview';
import MultiStepForm from './MultiStepForm';
import ConditionalLogic from './ConditionalLogic';
import FieldSuggestions from './FieldSuggestions';
import ExportJSONSchema from './ExportJSONSchema';
import EmbedCode from './EmbedCode';

interface FormBuilderState {
    fields: FormField[];
    validationRules: ValidationRule[];
    currentStep: number;
}

class FormBuilder extends Component<{}, FormBuilderState> {
    constructor(props: {}) {
        super(props);
        this.state = {
            fields: [],
            validationRules: [],
            currentStep: 0,
        };
    }

    addField = (field: FormField) => {
        this.setState(prevState => ({
            fields: [...prevState.fields, field],
        }));
    };

    removeField = (fieldId: string) => {
        this.setState(prevState => ({
            fields: prevState.fields.filter(field => field.id !== fieldId),
        }));
    };

    updateField = (updatedField: FormField[]) => {
        this.setState({ fields: updatedField });
    };

    setValidationRules = (rules: ValidationRule[]) => {
        this.setState({ validationRules: rules });
    };

    nextStep = () => {
        this.setState(prevState => ({
            currentStep: prevState.currentStep + 1,
        }));
    };

    previousStep = () => {
        this.setState(prevState => ({
            currentStep: Math.max(prevState.currentStep - 1, 0),
        }));
    };

    render() {
        return (
            <div>
                <DragDropUI fields={this.state.fields} setFields={fields => this.setState({ fields })} />
                <LivePreview fields={this.state.fields} />
                <MultiStepForm
                    currentStep={this.state.currentStep}
                    nextStep={this.nextStep}
                    previousStep={this.previousStep}
                />
                <ConditionalLogic fields={this.state.fields} onUpdate={this.updateField} />
                <FieldSuggestions setValidationRules={this.setValidationRules} />
                <ExportJSONSchema fields={this.state.fields} validationRules={this.state.validationRules} />
                <EmbedCode fields={this.state.fields} />
            </div>
        );
    }
}

export default FormBuilder;
