import React, { useState } from 'react';

const MultiStepForm = ({ steps, onSubmit }) => {
    const [currentStep, setCurrentStep] = useState(0);
    const [formData, setFormData] = useState({});

    const nextStep = () => {
        setCurrentStep((prevStep) => Math.min(prevStep + 1, steps.length - 1));
    };

    const prevStep = () => {
        setCurrentStep((prevStep) => Math.max(prevStep - 1, 0));
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (currentStep === steps.length - 1) {
            onSubmit(formData);
        } else {
            nextStep();
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {React.cloneElement(steps[currentStep], { onChange: handleChange, formData })}
            <div>
                {currentStep > 0 && <button type="button" onClick={prevStep}>Back</button>}
                <button type="submit">{currentStep === steps.length - 1 ? 'Submit' : 'Next'}</button>
            </div>
        </form>
    );
};

export default MultiStepForm;
